Luogu P1002 [NOIP2002 普及组] 过河卒 code C++<pre><code>#define fst first
#define sed second
#include &lt;iostream&gt;
#include &lt;set&gt; 
using namespace std;

typedef pair&lt;int, int&gt; PII;

const int N = 30;

long long dp[N][N];   // 到达点 i, j 的最短路径
set &lt;PII&gt; H;

int main() {
    PII c, b;   // b是马
    cin &gt;&gt; c.fst &gt;&gt; c.sed &gt;&gt; b.fst &gt;&gt; b.sed;
    b.fst++, b.sed++, c.fst++, c.sed++;

    H.insert({ b.fst, b.sed });
    H.insert({ b.fst - 1, b.sed - 2 });
    H.insert({ b.fst - 2, b.sed - 1 });
    H.insert({ b.fst - 1, b.sed + 2 });
    H.insert({ b.fst - 2, b.sed + 1 });
    H.insert({ b.fst + 1, b.sed - 2 });
    H.insert({ b.fst + 2, b.sed - 1 });
    H.insert({ b.fst + 1, b.sed + 2 });
    H.insert({ b.fst + 2, b.sed + 1 });

    dp[0][1] = 1;

    for (int i = 1; i &lt;= c.fst; i++)
        for (int j = 1; j &lt;= c.sed; j++)
            if (H.count({ i, j }))
                dp[i][j] = 0;
            else
                dp[i][j] = dp[i - 1][j] + dp[i][j - 1];

    cout &lt;&lt; dp[c.fst][c.sed];

    return 0;
}
</code></pre>

Luogu P7074 [CSP-J2020] 方格取数 code C++<pre><code>#include &lt;cstring&gt;
#include &lt;iostream&gt;
using namespace std;

typedef long long LL;

const int N=1e3+10;

int g[N][N];
LL dp[N][N][2];
int n, m;

int main(){
    //freopen("in.txt", "r", stdin); freopen("out.txt", "w", stdout);
    memset(g, -0x3f, sizeof g);
    memset(dp, -0x3f, sizeof dp);
    cin&gt;&gt;n&gt;&gt;m;
    for(int i=1; i&lt;=n; i++)
        for(int j=1; j&lt;=m; j++)
            scanf("%d", &amp;g[i][j]);

    dp[1][1][0]=dp[1][1][1]=g[1][1];
    for(int i=2; i&lt;=n; i++) // 用手导第一列
        dp[i][1][0] = dp[i][1][1] = dp[i-1][1][0] + g[i][1];
    //for(int i=n-1; i&gt;=1; i--)
    //    dp[i][1][1] = dp[i+1][1][1] + g[i][1];
    //for(int i=1; i&lt;=n; i++)
