    for(int j=2; j&lt;=m; j++){
        for(int i=1; i&lt;=n; i++) // 从上到下，从左边来，从上面来
            dp[i][j][0] = max(dp[i][j-1][0], dp[i-1][j][0]) + g[i][j];

        for(int i=n; i&gt;=1; i--) // 从下到上，从左边来，从下面来
