    for(int i=0; i&lt;5; i++)
        for(int j=0; j&lt;5; j++)
            backup[i][j]=g[i][j];
}

void rbf(){
    for(int i=0; i&lt;5; i++)
        for(int j=0; j&lt;5; j++)
            g[i][j]=backup[i][j];
}

void change(int x, int y){
    for(int i=0; i&lt;4; i++){
        int nx=x+dxy[i][0];
        int ny=y+dxy[i][1];
        if(nx&lt;0 || ny&lt;0) continue;
        g[nx][ny]^=1;
    }
    g[x][y]^=1;
}

// w是当前已经改变的次数
void dfs(int u, int w){

    if(w &gt; ans) return ;

    // 到达边界
