        // 保存当前地图
        bf();
        // 直接计算下面4层
        for(int i=1; i&lt;5 &amp;&amp; w&lt;=6; i++)
            for(int j=0; j&lt;5; j++)
                if(!g[i-1][j])
                    change(i, j), w++;

        if(w&lt;=6 &amp;&amp; check()) ans=min(ans, w);
        // 恢复回去
        rbf();
        return ;
    }

    // 改变
    change(0, u);
    dfs(u+1, w+1);
    change(0, u);

    // 不改变
    dfs(u+1, w);
}


