C++<pre><code>#pragma once
#include &lt;mutex&gt;
#include &lt;optional&gt;
#include &lt;queue&gt;

template &lt;typename T&gt;
class queue_safe {
   public:
    queue_safe() = default;
    ~queue_safe() = default;
    queue_safe(const queue_safe&amp;) = delete;
    queue_safe&amp; operator=(const queue_safe&amp;) = delete;
    queue_safe(queue_safe&amp;&amp;) = delete;
    queue_safe&amp; operator=(queue_safe&amp;&amp;) = delete;

    void push(T&amp;&amp; value) {
        std::lock_guard&lt;std::mutex&gt; lock(_mutex);
        _queue.push(std::move(value));
    }

    std::optional&lt;T&gt; pop() {
        std::lock_guard&lt;std::mutex&gt; lock(_mutex);
        if (_queue.empty()) {
            return std::nullopt;
        }
        T result = std::move(_queue.front());
        _queue.pop();
        return result;
    }

   private:
    std::queue&lt;T&gt; _queue;
    std::mutex _mutex;
};
