    ~不兼容类() {};
    std::string 功能() {
        return "一个接口不兼容的功能";
    }
};

class 适配器 {
private:
    不兼容类* 旧;
public:
    适配器() {};
    ~适配器() {};

    std::string 统一接口(不兼容类* 旧) {
        return 旧-&gt;功能();
    };
};

int main() {
    不兼容类* 旧 = new 不兼容类();
    适配器* 目标 = new 适配器();

    std::cout &lt;&lt; 目标-&gt;统一接口(旧);

    return 0;
}
</code></pre>

C++<pre><code>// FileCopy.cpp
#include &lt;fcntl.h&gt;  // open
#include &lt;unistd.h&gt; // read write
#include &lt;string.h&gt; // memset
#include &lt;iostream&gt; // std::cerr

int main(int argc, char *argv[]) {
    if(argc != 3){
        std::cerr &lt;&lt; "请输入源文件和目标文件的名称" &lt;&lt; std::endl;
        return 1;
    }

    // 以只读的方式打开
    int fd1 = open(argv[1], O_RDONLY);
    if(fd1 == -1){
        std::cerr &lt;&lt; "打开源文件失败" &lt;&lt; std::endl;
        return 1;
    }

    // 以只写的方式打开，如果不存在则创建，如果存在则清空，权限为 666
    int fd2 = open(argv[2], O_WRONLY | O_CREAT | O_TRUNC, 0666);
    if(fd2 == -1){
        std::cerr &lt;&lt; "打开目标文件失败" &lt;&lt; std::endl;
        return 1;
    }

    char buffer[64];
    while(true){
        memset(buffer, '\0', sizeof buffer);
        int len = read(fd1, buffer, sizeof buffer);

        if(len == -1){
            std::cerr &lt;&lt; "读取文件失败" &lt;&lt; std::endl;
            return 1;
        }

        if(len == 0) break;

        if(write(fd2, buffer, len) == -1){
            std::cerr &lt;&lt; "写入文件失败" &lt;&lt; std::endl;
            return 1;
        }
    };

    return 0;
}
</code></pre> <ul> <li>可以通过 <code>man open</code>，<code>man read</code> 等命令查看函数的使用方法</li> </ul> <p>运行：</p> <ol> <li>编译源文件 <code>g++ FileCopy.cpp -o FileCopy</code></li> <li>运行 <code>./FileCopy FileCopy.cpp FileCopy.cpp.bak</code></li> <li>查看文件 <code>cat FileCopy.cpp.bak</code><ul> <li>与 <code>FileCopy.cpp</code> 一致</li> </ul> </li> <li>使用系统调用跟踪工具 <code>strace ./FileCopy FileCopy.cpp FileCopy.cpp.bak</code><ul> <li>可以看到系统调用的详细信息</li> </ul> </li> </ol> <p></p>

