Go<pre><code>package main

import (
    "fmt"
    "sync"
    "time"
)

var (
    x    int64
    lock sync.Mutex
)

func addWithlock() {
    for i := 0; i &lt; 2e3; i++ {
        lock.Lock()
        x += 1
        lock.Unlock()
    }
}

func addWithoutlock() {
    for i := 0; i &lt; 2e3; i++ {
        x += 1
    }
}

func main() {

    x = 0
    for i := 0; i &lt; 5; i++ {
        go addWithoutlock() // 无锁加法
    }
    time.Sleep(time.Second)
    fmt.Println("无锁：", x)

    x = 0
    for i := 0; i &lt; 5; i++ {
        go addWithlock() // 有锁加法
    }
    time.Sleep(time.Second)
    fmt.Println("有锁：", x)
}
</code></pre> <p>输出：</p> Text Only<pre><code>无锁：9767
有锁：10000
