import (
    "fmt"
    "html/template"
    "net/http"
)

type U struct {
    Name   string
    Age    int
    Gender string
}

// 这两个参数分别用于向客户端发送响应和获取客户端发送的请求信息
func sayHello(w http.ResponseWriter, r *http.Request) {
    // 解析模板
    t, err := template.ParseFiles("./learn/hello.tmpl")
    if err != nil {
        fmt.Println("模板解析错误：%v", err)
        return
    }
    // 渲染模板

    u1 := U{
