        f[i][0]=1;
        for(int j=1; j&lt;=i; j++){
            // 计算
            f[i][j]=f[i-1][j-1]+f[i-1][j];

            // 大于1e9无意义，直接设置为INF，防止下面的值爆LL
            if(f[i][j]&gt;1e9) f[i][j]=INF; 

            // 找到目标值
            if(f[i][j]==n){
                // (1 + (i+1 - 1))*(i)/2  (a1+an)*(n)/2  （首项+末项）*（项数）/ 2
                cout&lt;&lt;1LL*(i+1)*(i)/2+j+1;
