const int N=1e4+10;

bool st[N]={1, 1};  // 0,1不是素数
int primes[N];

void make_primes(int n){// 生成 n 以内的素数
    for(int i=2; i&lt;=n; i++){
        if(!st[i]) primes[++(*primes)]=i;   // 登记素数
        for(int j=1; j&lt;=(*primes) &amp;&amp; primes[j]&lt;=n/i; j++){
            st[primes[j]*i]=1;              // 筛掉
            if(i%primes[j]==0) break;       // i 是 primes[j] 的倍数
        }   // 如果 i 是 primes[j] 的倍数, primes[j] 就是其最小质因子
    }       // 因此, 我们不必用其他质因子去筛了
}

PII q[10000000];

void solve(){
    int a, b; cin&gt;&gt;a&gt;&gt;b;

    int f=1,e=0;
    q[++e] = {a, 0};

    while(f&lt;=e &amp;&amp; q[f].fst != b){
        auto u = q[f];
        ++f;

        // 改变个位
        for(int i=1; i&lt;=9; i+=2){
            int n = (u.fst/10)*10 + i;
            if( !st[n] &amp;&amp; n!=u.fst )
                q[++e] = {n, u.sed+1};
        }
        // 改变十位
        for(int i=0; i&lt;=9; i++){
            int n = (u.fst/100)*100 + i*10 + u.fst%10;
            if( !st[n] &amp;&amp; n!=u.fst )
                q[++e] = {n, u.sed+1};
        }
        // 改变百位
        for(int i=0; i&lt;=9; i++){
            int n = (u.fst/1000)*1000 + i*100 + u.fst%100;
            if( !st[n] &amp;&amp; n!=u.fst )
                q[++e] = {n, u.sed+1};
        }
        // 改变千位
        for(int i=1; i&lt;=9; i++){
            int n = i*1000 + u.fst%1000;
            if( !st[n] &amp;&amp; n!=u.fst )
                q[++e] = {n, u.sed+1};
        }
    }
    cout&lt;&lt;q[f].sed&lt;&lt;"\n";
}

int main(){
    make_primes(1e4);    
    int T; cin&gt;&gt;T; 
    while(T--) solve();
    return 0;
}
