    int len = 1; while (len &lt; n + m - 1) len &lt;&lt;= 1;

    fft(a, len, 1), fft(b, len, 1);
    for (int i = 0; i &lt; len; i++) a[i] *= b[i];
    fft(a, len, -1);

    // 处理进位
    int k = 0;
    for (int i = 0, t = 0; i &lt;= n + m || t; i++) {
        t += a[i].real() / len + 0.5;
        b[k++] = (double)(t % 10);
        t /= 10;
    }

    // 去除高位的0
    while (k &gt; 1 &amp;&amp; !b[k - 1].real()) k--;
    string res;
    for (int i = k - 1; i &gt;= 0; i--) res += b[i].real() + '0';
    return res;
}
