public:
    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        std::stack&lt;int&gt; stk1, stk2, stk3;
        while(l1){
            stk1.push(l1-&gt;val);
            l1=l1-&gt;next;
        }
        while(l2){
            stk2.push(l2-&gt;val);
            l2=l2-&gt;next;
        }

        int add=0;
        while(stk1.size()||stk2.size()){
            if(stk1.size()){
                add += stk1.top();
                stk1.pop();
            }
            if(stk2.size()){
                add += stk2.top();
                stk2.pop();
            }
            stk3.push(add%10);
            add/=10;
