        if(add) stk3.push(add);

        ListNode *ans = new ListNode(stk3.top());
        stk3.pop();
        auto *pos = ans;

        while(stk3.size()){
            auto *t = new ListNode(stk3.top());
            pos-&gt;next = t;
            pos = pos-&gt;next;
            stk3.pop();
        }

        return ans;
    }
