    memset(dis, 0x3f, sizeof dis);
    memset(st, 0, sizeof st);
    memset(cnt, 0, sizeof cnt);

    queue&lt;int&gt; q;
    q.push(s), st[s]=1, dis[s]=0;

    while(q.size()){
        auto x = q.front(); q.pop(), st[x]=0;

        for(auto &amp;[y,z]: edge[x])
            if( dis[y] &gt; dis[x] + z ){
                dis[y] = dis[x] + z;

                cnt[y] = cnt[x] + 1;
                if(cnt[y] &gt; n) return 1;   // 负环, n是点的数量

                if(!st[y]) q.push(y), st[y]=1;
            }
    }
    return 0;
}

