typedef pair&lt;int, int&gt; PII;

const int N=8e3+10;

PII a[N];   // 数组
int n, q;

int p[N];    // 索引

int main(){
    cin&gt;&gt;n&gt;&gt;q;
    for(int i=1; i&lt;=n; i++){
        scanf("%d", &amp;a[i].fst);
        a[i].sed = i;
    }

    sort(a+1, a+1+n);
    for(int i=1; i&lt;=n; i++)
        p[ a[i].sed ] = i;

    for(int _=1; _&lt;=q; _++){
        int qq, x, v; scanf("%d", &amp;qq);

        if( qq==1 ){
            scanf("%d%d", &amp;x, &amp;v);

            // 定位到原数组的位置
            x = p[x];

            // 将a[i].fst 转换为 v, 检查其应该左移还是右移动
            if( a[x].fst &gt; v ){   // 左移动
                a[x].fst = v;
                for(int i=x; i&gt;=2; i--)
                    if(a[i] &lt; a[i-1]){
                        swap(a[i], a[i-1]);

                        p[ a[i].sed ] = i;
                        p[ a[i-1].sed ] = i-1;
                    }
                    else break;
            }else
            if( a[x].fst &lt; v ){   // 右移动
                a[x].fst = v;
                for(int i=x; i&lt;=n-1; i++)
                    if(a[i] &gt; a[i+1]){
                        swap(a[i], a[i+1]);

                        p[ a[i].sed ] = i;
                        p[ a[i+1].sed ] = i+1;
                    }
                    else break;
            }
        }else{
            scanf("%d", &amp;x);
            printf("%d\n", p[x]);
        }
    }

    return 0;
}
