using namespace std;

typedef long long LL;

int n, x, y, ans=1;

int main(){
    cin&gt;&gt;n&gt;&gt;x&gt;&gt;y;
    // ans 是左上角的数字，n 是当前圈的边长
    // i 是左上角的横纵坐标
    // j 是右下角的横纵坐标
    for(int i=1, j=n; i&lt;=j; i++, j--, ans+=n*4-4, n-=2)
        if(x==i){      // 上
            cout&lt;&lt;ans+(y-i);
            break;
        }
        else if(x==j){ // 下
            cout&lt;&lt;ans+(j-y)+n*2-2;
            break;
        }
        else if(y==i){ // 左
            cout&lt;&lt;ans+(j-x)+n*3-3;
            break;
        }
        else if(y==j){ // 右
            cout&lt;&lt;ans+(x-i)+n-1;
            break;
        } 

    return 0;
}
</code></pre>

