    for(int i=2; i&lt;=n; i++){
        c[i] = mmax;
        mmax = max(mmax, c[i] + b[i]);
    }

    mmax = c[1];
    for(int i=2; i&lt;=n; i++) mmax=max(mmax, c[i]);

    printf("%lld", mmax%p);

