void solve(){
    cnt=0;
    memset(res, '.', sizeof res);
    cin&gt;&gt;n&gt;&gt;k;
    for(int i=1; i&lt;=n; i++) cin&gt;&gt;g[i];

    for(int i=1; i&lt;=n; i++)
    if( g[i] == 'G' ){
        int j=min(n, i+k);  // 种植 G 的位置
        res[j] = 'G'; cnt++;// 种植
        i=j+k;              // 可以影响到的右边界
    }

    for(int i=1; i&lt;=n; i++)
    if( g[i] == 'H' ){
        int j=min(n, i+k);  // 种植 H 的位置
        while( res[j] == 'G' ) --j; // 思考:为什么--j的位置一定不会冲突?
        res[j] = 'H'; cnt++;
        i=j+k;
    }

    cout&lt;&lt;cnt&lt;&lt;"\n";
    for(int i=1; i&lt;=n; i++)
        cout&lt;&lt;res[i];
    cout&lt;&lt;"\n";
}

int main(){
