#include &lt;vector&gt;
#include &lt;cstring&gt;
using namespace std;

#define pb push_back

const int N=110;

int a[N], bk[N];
vector&lt;int&gt; h[N];   // 合成 i 所需要的金属
int n, k;

bool dfs(int u, int cntu){  // 能否获得 cntu 个材料 u
    if( a[u] &gt;= cntu ) {    // 已经拥有
        a[u] -= cntu;
        return 1;  
    }
    if( h[u].size()==0 )    // 不足且无法合成
        return 0;  

    cntu-=a[u], a[u]=0;     // 还需要cntu个a[u] 
    for(auto nu: h[u])      // 尝试使用 nu 进行合成
        if( !dfs(nu, cntu-a[u]) ) 
            return 0;
    return 1;
}

int main(){
