            int mi; cin&gt;&gt;mi;
            h[l].pb(mi);
        }
    }

    memcpy(bk, a, sizeof a);

    int l=a[n], r=1e6;
    while(l&lt;r){
        memcpy(a, bk, sizeof a);
        int mid = l+r+1&gt;&gt;1;
