    "Ox", "Tiger", "Rabbit", "Dragon", "Snake", "Horse", "Goat", "Monkey", "Rooster", "Dog", "Pig", "Rat",
    "Ox", "Tiger", "Rabbit", "Dragon", "Snake", "Horse", "Goat", "Monkey", "Rooster", "Dog", "Pig", "Rat",
    "Ox", "Tiger", "Rabbit", "Dragon", "Snake", "Horse", "Goat", "Monkey", "Rooster", "Dog", "Pig", "Rat"
};

int main(){
    int Q; cin&gt;&gt;Q;

    ans["Bessie"]=0;
    u["Bessie"]="Ox";

    while(Q--){
        string s[10];
        for(int i=1; i&lt;=8; i++) cin&gt;&gt;s[i];
        string A=s[1], B=s[8];
        u[A] = s[5];    // 可以直接确定 A 的生肖

        // 先找到 B 的位置
        int idxA, idxB;
        for(int i=12; i&lt;=23; i++) 
            if(sx[i]==u[B]){
                idxB=i;
                break;
            }

        if(s[4]=="previous"){
            // 再找到 A 的位置，A &lt; B
            idxA = idxB - 1;
