    string s; cin&gt;&gt;s;
    for(int i=0; i&lt;s.size()-1; i++)
        if(s[i] != s[i+1]){
            string t = string(1, s[i]) + string(1, s[i+1]);
            if(t=="NE" || t=="ES" || t=="SW" || t=="WN") res++;
            else res--;
        }

    if(res&gt;0) return "CW";
    return "CCW";
}

int main(){
    int T; cin&gt;&gt;T;
    while(T--) cout&lt;&lt;solve()&lt;&lt;"\n";
    return 0;
}
